{"query":"query postsFind($where: JSONObject, $order: JSONObject, $relations: [RelationsDto!]!) {
	result: postsFind(where: $where, order: $order, relations: $relations) {
		id
		createdAt
		updatedAt
		title
		content
		publishedAt
		isPublished
		tags {
			id
			createdAt
			updatedAt
			title
		}
		category {
			id
			createdAt
			updatedAt
			title
		}
	}
}","operationName":"postsFind","variables":{"where":{"auth":{"id":26}},"order":{"id":"desc"},"relations":[{"name":"category"}]}}

{"query":"query postsFilter($where: PostsDto!, $search: SearchDto!, $options: OptionsDto!, $relations: [RelationsDto!]) {
	result: postsFilter(where: $where, search: $search, options: $options, relations: $relations) {
		count
		group
		pages
		list {
			id
			createdAt
			updatedAt
			title
			content
			publishedAt
			isPublished
		}
	}
}","operationName":"postsFilter","variables":{"where":{"id":"{id} >= 10 and {id} <= 100"},"search":{"array":["2","3"],"fields":["id"],"or":true},"options":{"order":"id","desc":false,"skip":0,"limit":10,"page":1}}}

{"query":"mutation postsUpdate($id: Float!, $update: PostsDto!) {
	result: postsUpdate(id: $id, update: $update) {
		id
		createdAt
		updatedAt
		title
		content
		publishedAt
		isPublished
	}
}","operationName":"postsUpdate","variables":{"id":41,"update":{"title":"Новая запись+"}}}
